import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Image, SafeAreaView,Button,Alert, TouchableHighlight,TouchableWithoutFeedback, TouchableOpacity , TextInput} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';


export default function ChooseAccount({text , onPress}) { 
  const handlePress= () => console.log("text pressed");
    return (
      <SafeAreaView style={styles.body}>
                <Text style={styles.innerText}>בחר סוג משתמש</Text>

                <TouchableOpacity onPress={onPress}>
                <LinearGradient colors={['#5EE0D4', '#3b5998', '#192f6a']} style={styles.BusinessButton}/>
                  <View style={styles.BusinessButton}>
                    <Text style={styles.BusinessTitle}> עצמאים </Text>
                  </View>
                </TouchableOpacity>

                <TouchableOpacity onPress={onPress}>
                  <View style={styles.CpaButton}>
                    <Text style={styles.CpaTitle}> רואה חשבון </Text>
                  </View>
                </TouchableOpacity>
              
      </SafeAreaView>
    );
  }



  const styles = StyleSheet.create({
    body: {
      width: 390,
      height: 844,
      backgroundColor: '#F8F8FF',
    },
    innerText: {
      textAlign:'center',
      color: 'black',
      fontSize: 40,
      marginTop: 100,
      fontWeight: 'bold',
    },
    CpaButton:{
        position: 'absolute',
        width: 288,
        height: 88,
        left: 47,
        top: 300,
        backgroundColor: '#FFFFFF',
        shadowColor: 'rgba(169, 169, 169, 0.64)',
        shadowOffset: {
            height: 1.4,
            width: 0.1
    }
    },
    CpaTitle:{
        fontWeight: 'bold',
        fontSize: 34,
        color: '#6D6D6D',
    },
    BusinessButton:{
        position: 'absolute',
        width: 288,
        height: 88,
        left: 47,
        top: 150,
        shadowOffset: {
            height: 1.4,
            width: 0.1
        }
    },
    BusinessTitle:{
        fontWeight: 'bold',
        fontSize: 40,
        color: 'white',
    },
  });
