import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Image, SafeAreaView,Button,Alert, TouchableHighlight,TouchableWithoutFeedback, TouchableOpacity , TextInput} from 'react-native';



export default function ChooseAccount({text , onPress}) { 
  const handlePress= () => console.log("text pressed");
    return (
      <SafeAreaView style={styles.body}>
                <Text style={styles.innerText}>בחר סוג משתמש</Text>
                
                <TextInput style={styles.inputMail} value={text} keyboardType="numeric" placeholder="הזן מייל"></TextInput>             
                <TouchableOpacity onPress={onPress}>
                  <View style={styles.BusinessButton}>
                    <Text style={styles.buttonLoginTitle}> עמצאים </Text>
                  </View>
                </TouchableOpacity>
                <TouchableOpacity onPress={onPress}>
                  <View style={styles.CpaButton}>
                    <Text style={styles.CpaTitle}> רואה חשבון </Text>
                  </View>
                </TouchableOpacity>
              
      </SafeAreaView>
    );
  }



  const styles = StyleSheet.create({
    body: {
      width: 390,
      height: 844,
      backgroundColor: '#F8F8FF',
    },
    innerText: {
      textAlign:'center',
      color: 'black',
      fontSize: 40,
      marginTop: 100,
      fontWeight: 'bold',
    },
    inputMail:{
      fontSize: 18,
      textAlign: 'right',
      position: 'absolute',
      width: 303,
      height: 35,
      left: 55,
      top: 300,
      backgroundColor:'#F8F8FF',
      shadowColor: 'rgba(169, 169, 169, 0.64)',
      shadowOffset: {
      height: 1.4,
      width: 0.1
    }
    },
    CPA:{
        position: absolute,
        width: 288,
        height: 88,
        left: 47,
        top: 625,
        backgroundColor: '#FFFFFF',
       
      
        shadowColor: 'rgba(169, 169, 169, 0.64)',
        shadowOffset: {
        height: 1.4,
        width: 0.1
    }
    },
    CpaTitle:{
        fontWeight: 'bold',
        fontSize: 40,
        color: '#6D6D6D',
    },
    Business:{
        
    },
  });
