import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Image, SafeAreaView,Button,Alert, TouchableHighlight,TouchableWithoutFeedback, TouchableOpacity , TextInput ,Dimensions,  FlatList,Item,} from 'react-native';
import React, { useState } from "react";
import Clearing from './Icons/Clearing.png';
import TranzillaLogo from './Icons/TranzillaLogo.jpg';


export default function ClearingPage({text , onPress ,item}) { 
    const Item = (props) => {
        return (
          <View style={{alignItems: 'center'}}>
            <Text>{props.cost}</Text>
          </View>
        );
    }
    return (
      <SafeAreaView style={styles.body}>
        <Image source={Clearing} style={styles.ClearingBackground} />
            <View style={styles.container}>
                <View style={styles.subContainer}>
                    <Text style={styles.mainTitle}>אפשרויות סליקה</Text>
                </View>
            <View style={styles.FirstBoard} >
                <Image source={TranzillaLogo} style={styles.Logos} />
                <Item cost='כמה משלמים'></Item>
            </View>

            </View>        
        </SafeAreaView>
    );
}

  const styles = StyleSheet.create({
    ClearingBackground:{
      width:'100%',
      height:360,
    },
    body: {
      width: 390,
      height: 844,
      backgroundColor: 'white',
    },
    container: {
        width:'100%',
        height:'100%',
        borderRadius:32,
        backgroundColor: "#e9f0f7",
    },
    subContainer: {
        width:'88%',
        height:70,
        top:10,
        left:25,
        borderRadius:22,
        backgroundColor: "#7471F2",
        shadowColor: "#a9a9a9",
        shadowOffset: {
          width: 2,
          height: 3,
        },
        shadowOpacity: 0.27,
        shadowRadius: 4.65,
    },
    mainTitle: {
        fontSize:30,
        fontWeight:700,
        textAlign:'center',
        color:'white',
        marginTop:10,
    },
    FirstBoard: {
        width:'88%',
        height:230,
        top:30,
        left:25,
        borderRadius:22,
        backgroundColor: "white",
        shadowColor: "#a9a9a9",
        shadowOffset: {
          width: 2,
          height: 3,
        },
        shadowOpacity: 0.27,
        shadowRadius: 4.65,
    },
    Logos: {
        width:'55%',
        height:50,
        top: 10,
        left:95,
    },
  });
