import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Image, SafeAreaView,Button,Alert, TouchableHighlight,TouchableWithoutFeedback, TouchableOpacity , TextInput ,Picker} from 'react-native';
import { LinearGradient } from 'expo-linear-gradient';
import RNPickerSelect from "react-native-picker-select";


export default function ChooseAccount({text , onPress}) { 
  const handlePress= () => console.log("text pressed");
  const pickerStyle = {
	inputIOS: {
		color: 'white',
		paddingTop: 13,
		paddingHorizontal: 10,
		paddingBottom: 12,
	},
	inputAndroid: {
		color: 'white',
	},
	placeholderColor: 'white',
	underline: { borderTopWidth: 0 },
	icon: {
		position: 'absolute',
		backgroundColor: 'transparent',
		borderTopWidth: 5,
		borderTopColor: '#00000099',
		borderRightWidth: 5,
		borderRightColor: 'transparent',
		borderLeftWidth: 5,
		borderLeftColor: 'transparent',
		width: 0,
		height: 0,
		top: 20,
		right: 15,
	},
};
    return (
      <SafeAreaView style={styles.body}>
                <TouchableOpacity onPress={onPress}>
                  <View style={styles.button}>
                    <Text style={styles.buttonTitle}> פטור </Text>
                  </View>
                </TouchableOpacity>
                <TouchableOpacity>
                  <View style={styles.button2}>
                    <Text style={styles.buttonTitle2}> מורשה </Text>
                  </View>
                </TouchableOpacity>
                <Text style={styles.emailRegister} > מייל</Text> 
                <TextInput style={styles.inputMail} value={text} keyboardType="numeric" placeholder="הזן מייל"></TextInput>

                <Text style={styles.PhoneRegister} > פלאפון </Text> 

                <RNPickerSelect  placeholder={{ label: "050", value: "050" }}  onValueChange={(value) => console.log(value)} style={pickerStyle}
                 items={[
                     { label: "051", value: "051" },
                     { label: "052", value: "052" },
                     { label: "053", value: "053" },
                     { label: "054", value: "054" },
                     { label: "055", value: "055" },
                     { label: "058", value: "058" },

                 ]}
             />

                <TextInput style={styles.inputPhone} value={text} keyboardType="numeric" placeholder="הזן מספר פלאפון"></TextInput>

               
              
      </SafeAreaView>
    );
  }



  const styles = StyleSheet.create({
    body: {
        width: 390,
        height: 844,
        backgroundColor: '#F8F8FF',
    },
    button:{
        position: 'absolute',
        width: 128,
        height: 50,
        left: 220,
        top: 100,
        backgroundColor: 'white',
        borderRadius: 12,
        shadowColor: 'rgba(169, 169, 169, 0.64)',
        shadowOffset: {
        height: 1.2,
        width: 0.1
      }
    },
    buttonTitle:{
        fontSize:22,
        color: 'black',
        fontWeight:'700',
        textAlign:'center',
        marginLeft:15,
        marginTop:5,
    },
    button2:{
        position: 'absolute',
        width: 128,
        height: 50,
        left: 50,
        top: 100,
        backgroundColor: '#7471F2',
        borderRadius: 12,
        shadowColor: 'rgba(169, 169, 169, 0.64)',
        shadowOffset: {
        height: 1.2,
        width: 0.1
      }
    },
    buttonTitle2:{
        fontSize:22,
        color: 'white',
        fontWeight:'700',
        textAlign:'center',
        marginLeft:15,
        marginTop:5,
    },
    emailRegister:{
        position: 'absolute',
        width: 69,
        height: 13,
        left: 290,
        top: 200,
        textAlign:'right',
        fontWeight:600,
        fontSize:20,
        color:' #000000',
    },
    inputMail:{
        fontSize: 18,
        textAlign: 'right',
        position: 'absolute',
        width: 303,
        height: 35,
        left: 55,
        top: 242,
        backgroundColor:'#F8F8FF',
        shadowColor: 'rgba(169, 169, 169, 0.64)',
        shadowOffset: {
        height: 1.4,
        width: 0.1
      }
      },
     PhoneRegister:{
        position: 'absolute',
        width: 69,
        height: 13,
        left: 290,
        top: 300,
        textAlign:'right',
        fontWeight:600,
        fontSize:20,
        color:' #000000',
    },
    inputPhone:{
        fontSize: 18,
        textAlign: 'right',
        position: 'absolute',
        width: 303,
        height: 35,
        left: 55,
        top: 340,
        backgroundColor:'#F8F8FF',
        shadowColor: 'rgba(169, 169, 169, 0.64)',
        shadowOffset: {
        height: 1.4,
        width: 0.1
      }
      },
  });
