  },
  creditCardIcon: {
    position: "absolute",
    width: 36,
    height: 33,
    marginLeft: 15,
    top: 12,
  },
  ratingTitle: {
    position: "absolute",
    fontSize: 16,
    textAlign: "center",
    fontWeight: 700,
    color: "#6D6D6D",
    marginTop: 48,
    marginLeft: 292,
  },
  youtubeTitle: {
    position: "absolute",
    fontSize: 16,
    textAlign: "center",
    fontWeight: 700,
    color: "#6D6D6D",
    marginTop: 48,
    marginLeft: 232,
  },
  documentTitle: {
    position: "absolute",
    fontSize: 16,
    textAlign: "center",
    fontWeight: 700,
    color: "#6D6D6D",
    marginTop: 48,
    marginLeft: 142,
  },
  receiptTitle: {
    position: "absolute",
    fontSize: 16,
    textAlign: "center",
    fontWeight: 700,
    color: "#6D6D6D",
    marginTop: 48,
